import React, { useState } from 'react';
import { PieChart, Pie, Cell, Legend, Tooltip } from 'recharts';
import './VoterSummary.css';

const COLORS = ['#00C49F', '#FF8042']; // Colors: Voted, Not Voted

const ElectionDashboard = () => {
  const [registeredUsers, setRegisteredUsers] = useState(100);
  const [voters, setVoters] = useState(50);

  const votingRate = registeredUsers === 0 ? 0 : ((voters / registeredUsers) * 100).toFixed(2);
  const remaining = registeredUsers - voters;

  // Prepare Pie Chart Data
  const pieData = [
    { name: 'Voted', value: voters },
    { name: 'Not Voted', value: remaining > 0 ? remaining : 0 }
  ];

  const registerUser = () => {
    setRegisteredUsers(registeredUsers + 1);
  };

  const addVoter = () => {
    if (voters < registeredUsers) {
      setVoters(voters + 1);
    } else {
      alert('All registered users have already voted.');
    }
  };

  return (
    <div className="max-w-lg mx-auto p-6 bg-white rounded-2xl shadow-lg text-center">
      <h1 className="text-3xl font-bold mb-4">Election Dashboard</h1>

      <div className="mb-4">
        <p className="text-xl mb-2">Registered Users: <span className="font-semibold">{registeredUsers}</span></p>
        <button className="bg-green-500 text-white px-4 py-2 rounded mr-2" onClick={registerUser}>Register New User</button>
      </div>

      <div className="mb-4">
        <p className="text-xl mb-2">Voted Users: <span className="font-semibold">{voters}</span></p>
        <button className="bg-blue-500 text-white px-4 py-2 rounded" onClick={addVoter}>Add Voter</button>
      </div>

      <div className="mb-6">
        <h2 className="text-xl font-semibold mb-2">Voting Rate: <span className="text-purple-600">{votingRate}%</span></h2>

        {/* Pie Chart Section */}
        <PieChart width={300} height={300}>
          <Pie
            data={pieData}
            cx="50%"
            cy="50%"
            labelLine={false}
            label={({ name, percent }) => `${name}: ${(percent * 100).toFixed(0)}%`}
            outerRadius={100}
            dataKey="value"
          >
            {pieData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </div>
    </div>
  );
};

export default ElectionDashboard;
